 1156	 <script>
 1157	 // 标签页切换
 1158	 function showTab(tabName) {
 1159	 // 隐藏所有页面
 1160	 document.querySelectorAll('.main-content > div').forEach(page => {
 1161	 page.style.display = 'none';
 1162	 });
 1163	 
 1164	 // 显示选中页面
 1165	 document.getElementById(tabName + '-page').style.display = 'block';
 1166	 
 1167	 // 更新底部导航高亮
 1168	 document.querySelectorAll('.tab-item').forEach(item => {
 1169	 item.classList.remove('active');
 1170	 });
 1171	 event.target.closest('.tab-item').classList.add('active');
 1172	 
 1173	 // 更新导航栏标题
 1174	 const titles = {
 1175	 'home': '薄荷生活',
 1176	 'accounting': '智能记账',
 1177	 'period': '健康周期',
 1178	 'more': '功能中心'
 1179	 };
 1180	 document.getElementById('nav-title').textContent = titles[tabName];
 1181	 }
 1182	
 1183	 // 打开交易弹窗
 1184	 function openTransactionModal(type) {
 1185	 const modal = document.getElementById('transaction-modal');
 1186	 const title = document.getElementById('modal-title');
 1187	 const categoryGrid = document.getElementById('category-grid');
